INSERT INTO Luogo (destinazione_id, id, descrizione, immagine, nome) VALUES (1000, 1002, 'Secondo i romani la più bella fontana al mondo, secondo gli altri pure...', '/images/uploads/luogo-photos/ft.jpeg', 'Fontana di Trevi');
INSERT INTO Luogo (destinazione_id, id, descrizione, immagine, nome) VALUES (1000, 1003, 'La basilica su cui si erge la più grande cupola architettonica al mondo, realizzata da Brunelleschi, è uno dei simboli di Roma e del potere del Vaticano', '/images/uploads/luogo-photos/bsp.jpg', 'Basilica di San Pietro');
INSERT INTO Luogo (destinazione_id, id, descrizione, immagine, nome) VALUES (1000, 1004, 'Anticamente utilizzata come vasca per combattimenti navali, oggi capolavoro di arte in cui si sfidano i celebri architetti Bernini e Borromini a colpi di opere più audaci', '/images/uploads/luogo-photos/pn.jpeg', 'Piazza Navona');
INSERT INTO Luogo (destinazione_id, id, descrizione, immagine, nome) VALUES (1000, 1001, 'Un punto panoramico che offre una vista su Roma mozzafiato. Bonus: portaci la ragazza', '/images/uploads/luogo-photos/gianicolo.jpeg', 'Terrazza del Gianicolo');
