INSERT INTO Itinerario (destinazione_id, id, descrizione, nome, numgiorni) VALUES (1000, 1000, 'Il primo giorno a Roma, al mattino visita il Colosseo e il Palatino per immergerti nella storia antica della citta. Alla sera, passeggia per il centro storico, ammirando Fontana di Trevi e il Pantheon. Il secondo giorno, visita i Musei Vaticani e la Basilica di San Pietro al mattino. Alla sera, goditi una cena nei ristoranti del Trastevere.', 'Roma di notte', 2);
INSERT INTO Itinerario (destinazione_id, id, descrizione, nome, numgiorni) VALUES (1000, 1001, 'Esplora Roma in due giorni. Al mattino del primo giorno, scopri il Colosseo e il Foro Romano. Alla sera, passeggiata lungo il Lungotevere, ammirando Castel SantAngelo e Ponte SantAngelo. Al mattino del secondo giorno, visita i Musei Vaticani e la Basilica di San Pietro. Alla sera, cena nei ristoranti tipici del Trastevere.', 'Roma storica', 2);
INSERT INTO Itinerario (destinazione_id, id, descrizione, nome, numgiorni) VALUES (1000, 1002, 'Scopri Roma in tre giorni. Al mattino del primo giorno, visita il Quartiere EUR per il suo design architettonico unico. Alla sera, passeggiata attraverso Villa Borghese e visita Galleria Borghese. Al mattino del secondo giorno, esplora il quartiere Testaccio e goditi un pranzo tradizionale romano. Alla sera, visita il mercato di Campo de Fiori. Al mattino del terzo giorno, visita il Museo di Roma e passeggiata lungo il fiume Tevere alla sera.', 'Roma moderna', 3);
INSERT INTO Itinerario (destinazione_id, id, descrizione, nome, numgiorni) VALUES (1000, 1003, 'Scopri il meglio di Roma in quattro giorni. Al mattino del primo giorno, visita il Giardino degli Aranci e lAventino. Alla sera, passeggiata lungo il Lungotevere e visita Isola Tiberina. Al mattino del secondo giorno, esplora il quartiere Monti e visita la Basilica di Santa Maria Maggiore. Alla sera, cena nei ristoranti del quartiere Pigneto. Al mattino del terzo giorno, visita il quartiere Parioli e goditi un pranzo in uno dei suoi ristoranti. Alla sera, passeggiata lungo il fiume Tevere.', 'Roma artistica', 4);
INSERT INTO Itinerario (destinazione_id, id, descrizione, nome, numgiorni) VALUES (1000, 1004, 'Esplora Roma in cinque giorni. Al mattino del primo giorno, visita il Colosseo e il Palatino. Alla sera, cena nei ristoranti del centro storico. Al mattino del secondo giorno, visita i Musei Vaticani e la Basilica di San Pietro. Alla sera, passeggiata lungo il Lungotevere, ammirando Castel SantAngelo e Ponte SantAngelo. Al mattino del terzo giorno, scopri il quartiere EUR. Alla sera, visita Villa Borghese e Galleria Borghese. Al mattino del quarto giorno, esplora il quartiere Testaccio e goditi un pranzo tradizionale romano. Alla sera, cena nei ristoranti di Campo de Fiori. Al mattino del quinto giorno, visita il Museo di Roma e passeggiata lungo il fiume Tevere alla sera.', 'Roma gastronomica', 5);
INSERT INTO Attivita (destinazione_id, id, descrizione, immagine, nome) VALUES (1000, 1000, 'Esperienza suggestiva, ottimale nel periodo estivo', '/images/uploads/attivita-photos/passeggiata-lungotevere.jpeg', 'Passeggiata sul Longotevere');
